mapper.insert:
INSERT INTO RESERVE_INFO VALUES(
	TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS'), #{member_id}, #{room_no}, #{reserve_price}, #{reserve_name}
	, #{reserve_email}, #{reserve_phone}, #{reserve_start_time}, #{reserve_end_time}
	, TO_DATE(#{reserve_date}, 'YY/MM/DD'), #{payment_method_type}, SYSDATE, #{tid}
)

mapper.update:
UPDATE RESERVE_INFO
SET (ROOM_NO, RESERVE_PRICE, RESERVE_NAME
, RESERVE_EMAIL, RESERVE_PHONE ,RESERVE_START_TIME ,RESERVE_END_TIME
,RESERVE_COMMENT , PAYMENT_METHOD_TYPE, RESERVE_PAY, TID
)
= (#{room_no}, #{reserve_price}, #{reserve_name}
	, #{reserve_email}, #{reserve_phone}, #{reserve_start_time}, #{reserve_start_time}
	, #{reserve_date}, #{payment_method_type}, #{reserve_pay}, #{tid}
	)
WHERE MEMBER_ID=#{member_id} AND RESERVE_NO=#{reserve_no}

mapper.select:
SELECT TO_CHAR(b.dt, 'YYYY-MM') AS chart_date , NVL(SUM(a.cnt), 0) chart_cnt FROM ( SELECT TO_CHAR(reserve_date, 'YYYY-MM-DD') AS chart_date , COUNT(*) cnt FROM reserve_info WHERE room_no in (select room_no from study_room where p_no in (select p_no from place_info where area_code
in (select area_code from area where SIDO_NAME = #{sido_name})
= #{area_code}
) ) and reserve_date BETWEEN add_months(TRUNC(SYSDATE, 'MM'), -5) AND LAST_DAY(SYSDATE) GROUP BY reserve_date ) a
, ( SELECT add_months(TRUNC(SYSDATE, 'MM'), -5) + LEVEL - 1 AS dt FROM dual CONNECT BY LEVEL <= LAST_DAY(SYSDATE) - (add_months(TRUNC(SYSDATE, 'MM'), -5) + 1) ) b WHERE b.dt = a.chart_date(+) GROUP BY TO_CHAR(b.dt, 'YYYY-MM') ORDER BY TO_CHAR(b.dt, 'YYYY-MM')

SELECT TO_CHAR(b.dt, 'YYYY-MM') AS chart_date , NVL(SUM(a.price), 0) chart_price FROM ( SELECT TO_CHAR(reserve_date, 'YYYY-MM-DD') AS chart_date , sum(reserve_price) price FROM reserve_info WHERE room_no in (select room_no from study_room where p_no in (select p_no from place_info where area_code
in (select area_code from area where SIDO_NAME = #{sido_name})
= #{area_code}
) ) and reserve_date BETWEEN add_months(TRUNC(SYSDATE, 'MM'), -5) AND LAST_DAY(SYSDATE) GROUP BY reserve_date ) a
, ( SELECT add_months(TRUNC(SYSDATE, 'MM'), -5) + LEVEL - 1 AS dt FROM dual CONNECT BY LEVEL <= LAST_DAY(SYSDATE) - (add_months(TRUNC(SYSDATE, 'MM'), -5) + 1) ) b WHERE b.dt = a.chart_date(+) GROUP BY TO_CHAR(b.dt, 'YYYY-MM') ORDER BY TO_CHAR(b.dt, 'YYYY-MM')
